import { createSlice } from "@reduxjs/toolkit";
import fetchStories, { newStory } from "../fetchStories";
import { StoryObj } from "../UserContext";

export const storiesSlice = createSlice({
    name: 'stories',
    initialState: {
        data: [] as StoryObj[],
        loading: false
    },
    reducers: {
    },
    extraReducers: (builder) => {
        builder
        .addCase(fetchStories.fulfilled, (state, action) => {
            state.data = action.payload;
            state.loading = false;
        })
        .addCase(newStory.fulfilled, (state,action)=>{

        })
    }
})

export const storySlice = createSlice({
    name: 'story'
})

export const userStoriesSlice = createSlice({
    name: 'userStories',
    initialState: {
        data: [] as StoryObj[],
        loading: false
    },
    reducers: {}
})

export const storiesReducer = storiesSlice.reducer;
