import { useState } from "react";
import { useUser } from "./UserContext";

interface StoryFormProps {
  onStorySubmit: () => void;
}

function StoryForm({ onStorySubmit }: StoryFormProps) {
  const { isLoggedIn, newStory } = useUser();
  const [formValues, setFormValues] = useState({
    author: "",
    title: "",
    url: "",
  });

  return (
    <form
      action="#"
      className="hidden"
      id="story-form"
      onSubmit={(e) => {
        e.preventDefault();
        if (user.user.username) {
          newStory(formValues.author, formValues.title, formValues.url);
        }
        setFormValues({ author: "", title: "", url: "" });
      }}
    >
      <div>
        <label htmlFor="create-author">author</label>
        <input
          id="create-author"
          value={formValues.author}
          required
          placeholder="author name"
          onChange={(e) => {
            setFormValues({ ...formValues, author: e.target.value });
          }}
        />
      </div>
      <div>
        <label htmlFor="create-title">title</label>
        <input
          id="create-title"
          value={formValues.title}
          required
          placeholder="story title"
          onChange={(e) => {
            setFormValues({ ...formValues, title: e.target.value });
          }}
        />
      </div>
      <div>
        <label htmlFor="create-url">url</label>
        <input
          id="create-url"
          value={formValues.url}
          required
          type="url"
          placeholder="story url"
          onChange={(e) => {
            setFormValues({ ...formValues, url: e.target.value });
          }}
        />
      </div>
      <button type="submit">submit</button>
      <hr />
    </form>
  );
}

export default StoryForm;
