import { StoryObj } from "./UserContext";
import Story from "./Story";
import "./css/stories.css";
import { useSelector } from "react-redux";
import { RootState } from "./store/types";
import { isLoggedIn } from "./App";
import { selectIsFavorite } from "./store/selectors";
import { selectFavorites } from "./store/selectors";

function StoryList({
  stories,
  loggedIn,
}: {
  stories: StoryObj[];
  loggedIn: boolean;
}) {
  const isFavoriteSelector = selectIsFavorite(); // Get the selector function

  const favorites = useSelector((state: RootState) => state.user.favorites);

  const isFavorite = (story: StoryObj) => {
    return isFavoriteSelector(state, story); // Pass the state and story to the selector
  };
  // const favorites = useSelector((state: RootState) => state.user.favorites);

  // const isFavorite = selectIsFavorite(); 
  // const isFavorite = (story: StoryObj) => {
  //   return favorites.some((favorite: StoryObj) => {
  //     return favorite.storyId === story.storyId;
  //   });
  // };


  return (
    <ol className="stories-list">
      {stories.map((story) => (
        <Story
          story={story}
          key={story.storyId}
          isFavorite={isFavorite(story)}
          loggedIn={isLoggedIn}
        />
      ))}
    </ol>
  );
}

export default StoryList;
