import { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Navbar from "./Navbar";
import AccountForms from "./AccountForms";
import fetchStories from "./fetchStories";
import StoryList from "./StoryList";
import StoryForm from "./StoryForm";
import Favorites from "./Favorites";
import { StoryObj, useUser } from "./UserContext";

interface toggleFormsProps {
  formType: "account" | "story";
}

function App() {
  const [stories, setStories] = useState<StoryObj[]>([]);
  const [loading, setLoading] = useState(true);
  const [showAccountForms, setShowAccountForms] = useState(false);
  const [showStoryForm, setShowStoryForm] = useState(false);
  const { isLoggedIn, user } = useUser();

  const toggleForms = ({ formType }: toggleFormsProps) => {
    if (formType === "account") {
      setShowAccountForms(!showAccountForms);
    }
    if (formType === "story") {
      setShowStoryForm(!showStoryForm);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const storiesData = await fetchStories();
        setStories(
          storiesData.map(
            (story: {
              author: string;
              createdAt: string;
              storyId: string;
              title: string;
              updatedAt: string;
              url: string;
              username: string;
            }) => {
              return {
                story: {
                  author: story.author,
                  createdAt: story.createdAt,
                  storyId: story.storyId,
                  title: story.title,
                  updatedAt: story.updatedAt,
                  url: story.url,
                  username: story.username,
                },
              };
            }
          )
        );
      } catch (error) {
        console.error("Error fetching stories.", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <BrowserRouter>
      <div className="app">
        <Navbar
          loggedIn={isLoggedIn}
          onLoginClick={() => toggleForms({ formType: "account" })}
          onSubmitClick={() => toggleForms({ formType: "story" })}
        />
        {showAccountForms && (
          <AccountForms
            onLoginSubmit={() => toggleForms({ formType: "account" })}
          />
        )}
        {showStoryForm && (
          <StoryForm onStorySubmit={() => toggleForms({ formType: "story" })} />
        )}
        <Routes>
          <Route path="/" element={<StoryList stories={stories} />} />
          <Route
            path="/favorites"
            element={<Favorites />}
          />
          <Route path="/mystories" />
        </Routes>
        <section className="stories-list">
          {loading ? <div>Loading...</div> : <StoryList stories={stories} />}
        </section>
      </div>
    </BrowserRouter>
  );
}

export default App;
