import { createSelector } from "@reduxjs/toolkit";
import { RootState, StoryObj } from "./types";

export const selectFavorites = (state: RootState) => state.user.favorites;

export const selectIsFavorite = createSelector(
    [] as StoryObj,
    (favorites) => (story: StoryObj) => {
        return favorites.some((favorite: StoryObj) => {
            return favorite.storyId === story.storyId;
        });
    })
