import { createSlice } from "@reduxjs/toolkit";
import fetchStories, { newStory } from "../fetchStories";
import { StoryObj } from "./types"

export const storiesSlice = createSlice({
    name: 'stories',
    initialState: {
        data: [] as StoryObj[],
        loading: false
    },
    reducers: {
    },
    extraReducers: (builder) => {
        builder
        .addCase(fetchStories.fulfilled, (state, action) => {
            state.data = action.payload;
            state.loading = false;
        })
        .addCase(newStory.fulfilled, (state,action)=>{

        })
    }
})

export const storySlice = createSlice({
    name: 'story',
    initialState: {
        data: {
            author: "",
            createdAt: "",
            storyId: "",
            title: "",
            updatedAt: "",
            url: "",
            username: "",
        } as StoryObj,
        isFavorite: false,
    },
    reducers: {}
})

export const userStoriesSlice = createSlice({
    name: 'userStories',
    initialState: {
        data: [] as StoryObj[],
        loading: false
    },
    reducers: {},
    extraReducers: (builder) => {
        builder
        {
            author: string;
            createdAt: string;
            storyId: string;
            title: string;
            updatedAt: string;
            url: string;
            username: string;
        }
    }
})

export const storiesReducer = storiesSlice.reducer;
