import { createSlice } from "@reduxjs/toolkit";
import loginUser from "../fetchUser";

const initialState = {
    createdAt: "",
    favorites: [],
    name: "",
    stories: [],
    updatedAt: "",
    username: "",
    status: "idle",
};

export const userSlice = createSlice({
    name: "user",
    initialState,
    reducers: {
        autoLogin: (state, action) => {
            const savedUser = localStorage.getItem("username");
            const savedToken = localStorage.getItem("token");

            loginUser({savedUser, savedPassword})
        },
        signup: (state, action) => { },
        logout: (state) => {
            state.createdAt = "";
            state.favorites = [];
            state.name = "";
            state.stories = [];
            state.updatedAt = "";
            state.username = "";
            state.status = "idle";

            localStorage.clear();
        },
        update: (state, action) => { },
    },
    extraReducers: (builder) => {
        builder
            .addCase(loginUser.pending, (state) => {
                state.status = "loading"
            })
            .addCase(loginUser.fulfilled, (state, action) => {
                console.log(action.payload)
                state.username = action.payload.user.username;
                state.updatedAt = action.payload.user.updatedAt;
                state.createdAt = action.payload.user.createdAt;
                state.favorites = action.payload.user.favorites;
                state.name = action.payload.user.name;
                state.stories = action.payload.user.stories;
                state.status = "succeeded"

                localStorage.setItem('token', action.payload.token);
                localStorage.setItem('username', action.payload.user.username);
            });
    },
});

export const { autoLogin, signup, logout } = userSlice.actions;
export const userReducer = userSlice.reducer;
