import { StoryObj, useUser } from "./UserContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTrashAlt,
  faStar as faStarRegular,
} from "@fortawesome/free-regular-svg-icons";
import { faStar as faStarSolid } from "@fortawesome/free-solid-svg-icons";

export default function UserStory({
  story,
  isFavorite,
}: {
  story: StoryObj;
  isFavorite: boolean;
}) {
  const { addOrDeleteFavorite } = useUser();
  const url = new URL(story.story.url);
  const hostname = url.hostname;

  const handleStarClick = async (story: StoryObj) => {
    try {
      console.log("star clicked");
      const fetchMethod = isFavorite ? "delete" : "add";
      await addOrDeleteFavorite(fetchMethod, story.story.storyId);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <li className={story.story.storyId}>
      <div>
        <span className="trash-can">
          <FontAwesomeIcon icon={faTrashAlt} />
        </span>
        <span className="star" onClick={() => handleStarClick(story)}>
          <FontAwesomeIcon icon={isFavorite ? faStarSolid : faStarRegular} />
        </span>
        <a href={story.story.url}>{story.story.title}</a>
        <small>{hostname}</small>
        <div className="story-author">{story.story.author}</div>
        <div className="story-posted-by">posted by {story.story.username}</div>
      </div>
    </li>
  );
}
