import { useEffect, useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Navbar from "./Navbar";
import AccountForms from "./AccountForms";
import fetchStories from "./fetchStories";
import StoryList from "./StoryList";
import StoryForm from "./StoryForm";
import Favorites from "./Favorites";
import { StoryObj, useUser } from "./UserContext";
import MyStories from "./MyStories";

function App() {
  const [stories, setStories] = useState<StoryObj[]>([]);
  const [loading, setLoading] = useState(true);
  const [showStoryForm, setShowStoryForm] = useState(false);
  const { isLoggedIn } = useUser();
  const navigate = useNavigate();

  const toggleSubmitForm = () => {
    setShowStoryForm(!showStoryForm);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const storiesData: StoryObj[] = await fetchStories();
        setStories(storiesData);
      } catch (error) {
        console.error("Error fetching stories.", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    navigate("/");
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="app">
      <Navbar loggedIn={isLoggedIn} onSubmitClick={() => toggleSubmitForm()} />
      {showStoryForm && <StoryForm onStorySubmit={() => toggleSubmitForm()} />}
      <Routes>
        <Route path="/" element={<StoryList stories={stories} />} />
        <Route path="login" element={<AccountForms />} />
        <Route path="/submit" element/>
        <Route path="/favorites" element={<Favorites />} />
        <Route path="/mystories" element={<MyStories />} />
      </Routes>
    </div>
  );
}

export default App;
