import { StoryObj, useUser } from "./UserContext";
import Story from "./Story";
import UserStory from "./UserStory";
import "./css/stories.css";

function Favorites({ favorites }: { favorites: StoryObj[] }) {
  const { ownStories, user } = useUser();
  const isFavorite = (story: StoryObj) => {
    return favorites.some(
      (favorite) => {
        return favorite.story.storyId === story.story.storyId;
      }
    );
  };

  return (
    <ol className="stories-list">
      {ownStories.map((story) => (
        <UserStory
          story={story}
          key={story.story.storyId}
          isFavorite={isFavorite(story)}
        />
      ))}
      {favorites.map((story) => (
        <Story
          story={story}
          key={story.story.storyId}
          isFavorite={isFavorite(story)}
        />
      ))}
    </ol>
  );
}

export default Favorites;
