import { createAsyncThunk } from "@reduxjs/toolkit";

export const BASE_URL = "https://hack-or-snooze-v3.herokuapp.com";

const loginUser = createAsyncThunk(
  `user/login`,
  async ({ username, password }: { username: string; password: string }) => {
    const response = await fetch(BASE_URL + "/login", {
      method: "POST",
      body: JSON.stringify({
        user: {
          password,
          username,
        },
      }),
    });
    console.log(response);
    if (!response.ok) {
      throw new Error("Login failed");
    }

    const userData = await response.json();
    console.log("User data on login:", userData);

    return userData;
  }
);

const autoLogin = createAsyncThunk(
  "user/autoLogin",
  async ({ username, token }: { username: string; token: string }) => {
    const response = await fetch(
      `${BASE_URL}/users/${username}?token=${token}`,
      {
        method: "GET",
      }
    );
    console.log(response);
    if (!response.ok) {
      throw new Error("Login failed");
    }

    const userData = await response.json();
    console.log("User data on login:", userData);

    return userData;
  }
);

export default loginUser;
