import { useState } from "react";
import { useUser } from "./UserContext";

function StoryForm() {
  const dispatch = useD
  const [formValues, setFormValues] = useState({
    author: "",
    title: "",
    url: "",
  });

  return (
    <form
      action="#"
      className="new-story-form"
      id="story-form"
      onSubmit={(e) => {
        e.preventDefault();
        newStory(formValues.author, formValues.title, formValues.url);
        setFormValues({ author: "", title: "", url: "" });
      }}
    >
      <div className="form-field">
        <label htmlFor="create-author">author</label>
        <input
          id="create-author"
          value={formValues.author}
          required
          onChange={(e) => {
            setFormValues({ ...formValues, author: e.target.value });
          }}
        />
      </div>
      <div className="form-field">
        <label htmlFor="create-title">title</label>
        <input
          id="create-title"
          value={formValues.title}
          required
          onChange={(e) => {
            setFormValues({ ...formValues, title: e.target.value });
          }}
        />
      </div>
      <div className="form-field">
        <label htmlFor="create-url">url</label>
        <input
          id="create-url"
          value={formValues.url}
          required
          type="url"
          onChange={(e) => {
            setFormValues({ ...formValues, url: e.target.value });
          }}
        />
      </div>
      <button type="submit">submit</button>
    </form>
  );
}

export default StoryForm;
